static bool parse_integer(const char* text, int base) {
    /* convert number */
    yylval.INTEGER = (int)strtol(text, NULL, base);

    /* peek next character */
    char c = input();
    if (c != 0) unput(c);

    if (c == '.' || c == '_' || isalnum(c))
        return false;
    else
        return true;
}

static bool parse_bitmap(const char* text) {
    init();

    /* convert number */
    yylval.INTEGER = 0;
    const char* p;
    for (p = text; *p; p++) {
        if (*p == '-') yylval.INTEGER *= 2;
        else if (*p == '#') yylval.INTEGER = yylval.INTEGER * 2 + 1;
        else break;
    }

    if (*p == '"')
        return true;
    else
        return false;
}

