static int peek_token(void) {
    YYSTYPE savelval = yylval;
    yynexttok = yylex_1();
    yynextlval = yylval;
    yylval = savelval;
    return yynexttok;
}

static int get_next_token(void) {
    assert(yynexttok >= 0);
    int tok = yynexttok;
    yynexttok = -1;
    yylval = yynextlval;
    return tok;
}

static void clear_queue(void) {
    yynexttok = -1;
}

int yylex(void) {
    /* get next token */
    int tok = (yynexttok >= 0) ? get_next_token() : yylex_1();
    switch (yystate) {
    case AT_LABEL:
        switch (tok) {
        case '.':
            switch (peek_token()) {
            case IDENT:
                if (opt_dotdirective && is_directive(yynextlval.IDENT.id)) {
                    yystate = AT_OPERAND;
                    clear_queue();
                    return yynextlval.IDENT.id;
                }
                else {
                    yystate = AT_OPCODE;
                    yylval.LABEL = yynextlval.IDENT.name;
                    clear_queue();
                    return LABEL;
                }
            default:
                yystate = AT_OPERAND;
                return tok;
            }
        case IDENT:
            if (opt_labelcol1 && yylval.IDENT.atbol) {
                yystate = AT_OPCODE;
                yylval.LABEL = yylval.IDENT.name;
                return LABEL;
            }
            else {
                switch (peek_token()) {
                case ':': {
                    yystate = AT_OPCODE;
                    yylval.LABEL = yylval.IDENT.name;
                    clear_queue();
                    return LABEL;
                }
                case IDENT: {
                    if (is_opcode_or_directive(yynextlval.IDENT.id)) {		/* LABEL OPCODE */
                        yystate = AT_OPCODE;
                        yylval.LABEL = yylval.IDENT.name;
                        return LABEL;
                    }
                    else {							/* OPCODE IDENT */
                        yystate = AT_OPERAND;
                        return (is_opcode_or_directive(yylval.IDENT.id)) ?
                               yylval.IDENT.id : IDENT;
                    }
                }
                default:
                    yystate = AT_OPERAND;
                    return (is_opcode_or_directive(yylval.IDENT.id)) ?
                           yylval.IDENT.id : IDENT;
                }
            }
        case '\n':
            yystate = AT_LABEL;
            return tok;
        default:
            yystate = AT_OPERAND;
            return tok;
        }
    case AT_OPCODE:
        switch (tok) {
        case IDENT:
            yystate = AT_OPERAND;
            return (is_opcode_or_directive(yylval.IDENT.id)) ?
                   yylval.IDENT.id : IDENT;
        case '\n':
            yystate = AT_LABEL;
            return tok;
        default:
            return tok;
        }
    case AT_OPERAND:
        switch (tok) {
        case '\n':
            yystate = AT_LABEL;
            return tok;
        case IDENT:
            return (is_flag_or_register(yylval.IDENT.id)) ?
                   yylval.IDENT.id : IDENT;
        default:
            return tok;
        }
    default:
        assert(0); return 0;
    }
}

